import React , {useContext} from 'react';
import { GlobalContext} from '../Context/GlobalState';

export const Transaction = ({ transactions}) => {

  const { deleteTransaction } = useContext(GlobalContext);
  const sign = transactions.amount < 0 ? '-': '+';
    //sign ma hmny ya btaya hai k agr amount ki value less than 0 ho tou amount k sth
    //minus lgae wrna plus sign lgae.
  return (
    <li className={transactions.amount < 0 ? 'minus' : 'plus'}>
      {transactions.text} <span>{sign}PKR&nbsp;{Math.abs(transactions.amount)}</span><button 
      onClick={() => deleteTransaction(transactions.id)} class="delete-btn">x</button>
    </li> 
  )
}
